    private void Register_textMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_Register_textMouseClicked
        // REGISTER BUTTON
        
        Admin mainAdmin = new Admin("Admin",21);
        
        String strEmpt = new String(Password_field.getPassword());
        if(((Username_textfield.getText().equalsIgnoreCase("")) || (Username_textfield.getText().equalsIgnoreCase("Username"))) && ((strEmpt.equalsIgnoreCase("")) || strEmpt.equalsIgnoreCase("yourpassword"))){
  
            Enter_admin_account OpenPrompt = new Enter_admin_account();
            OpenPrompt.setLocationRelativeTo(this);
            OpenPrompt.setVisible(true);
        }
        
        if(Username_textfield.getText().equalsIgnoreCase("")){
            Username_textfield.setText("Username");
        }
        
        if(strEmpt.equalsIgnoreCase("")){
            Password_field.setText("yourpassword");
        }
        
        boolean ifAdmin = false;
        if(Username_textfield.getText().equals(mainAdmin.getName()) && strEmpt.equals(mainAdmin.adminPassword)){
            Register_Form OpenRegister = new Register_Form();
            OpenRegister.setLocationRelativeTo(this);
            OpenRegister.setVisible(true);
            ifAdmin = true;
        }else if (!(strEmpt.equals("yourpassword")) && !(Username_textfield.getText().equals("Username")) && !(ifAdmin) ){
            Enter_admin_account OpenPrompt = new Enter_admin_account();
            OpenPrompt.setLocationRelativeTo(this);
            OpenPrompt.setVisible(true);
        }
        
        
    }//GEN-LAST:event_Register_textMouseClicked

