    private void Login_textMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_Login_textMouseClicked
        boolean no_input = false;
        String strEmpt = new String(Password_field.getPassword());
        String username;
        String password;
        
        
        
        try {
           
            Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3306/clientdb", "root", "admin1");
            Statement st = con.createStatement();
        
        if(((Username_textfield.getText().equalsIgnoreCase("")) || (Username_textfield.getText().equalsIgnoreCase("Username"))) && ((strEmpt.equalsIgnoreCase("")) || strEmpt.equalsIgnoreCase("yourpassword"))){
            no_input = true;
            Enter_account OpenPrompt = new Enter_account();
            OpenPrompt.setLocationRelativeTo(this);
            OpenPrompt.setVisible(true);
        }      
        if(Username_textfield.getText().equalsIgnoreCase("")){
            Username_textfield.setText("Username");
        }
        
        if(strEmpt.equalsIgnoreCase("")){
            Password_field.setText("yourpassword");
        }
        
        if (checkAccount(Username_textfield.getText(), strEmpt)){
            
            username = Username_textfield.getText();
            password = strEmpt;
            After_login after_login = new After_login(username, password);
            after_login.setLocationRelativeTo(this);
            after_login.setVisible(true);
            this.dispose();
        }else{
            if(!no_input){
                Invalid_account invalidAccount = new Invalid_account();
                invalidAccount.setVisible(true);
                invalidAccount.setLocationRelativeTo(this);
            }   
        }
        }
        catch (SQLException ex) {
           ex.printStackTrace();
        }
        
        
    }//GEN-LAST:event_Login_textMouseClicked

    
    public boolean checkAccount (String x, String y){
        
        try{
            Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3306/clientdb", "root", "admin1");
                Statement st = con.createStatement();

            String hold;

            ResultSet rs = st.executeQuery("SELECT Password FROM clientinfo WHERE Name = " + "'" + x +"'");

            if (rs.next()){
                hold = rs.getString("Password");
            }
            else {
                return false;
            }
            if (y.equals(hold)){
                return true;
            }
        
        } catch (SQLException e){
            e.printStackTrace();
        }
        return false;
        
    }
    
